string script = @"function printReport(rid, condition) {
        require([
            'Sage/MainView/ReportMgr/ReportWizardController',
            'Sage/MainView/ReportMgr/Crystal/CrystalReportsUtility',
            'Sage/Reporting/Enumerations'
        ], function (ReportWizardController, CrystalReportsUtility, CrystalEnumerations) {
            var condition = CrystalReportsUtility.getCurrentEntityCondition();
            var options = {
                reportId: rid,
                reportOptions: {
                    wizardOptions: {
                        hiddenSteps: [CrystalEnumerations.CrystalReportWizardStep.Conditions, CrystalEnumerations.CrystalReportWizardStep.ExportOptions]
                    },
                    scheduleOptions: {
                        executionType: CrystalEnumerations.ExecutionType.OnDemand
                    },
                    exportOptions: {
                        outputFormat: CrystalEnumerations.ReportExportFormat.Excel
                    },
                    conditionOptions: {
                        conditions: [condition],
                        conditionsConnector: CrystalEnumerations.ReportConditionConnector.And
                    }
                }
            };
            ReportWizardController.startWizard(options);
        });
    }";

ScriptManager.RegisterStartupScript(this, GetType(), "createExcelReport", script, true);

